"use strict";
module.exports = (option) => {
    const config = {
        name: 'comment',
        define: () => ({
            COMMENT_OPTIONS: option
        }),
        plugins: [
            /** typescript 支持 */
            ['typescript']
        ]
    };
    if (option.type === 'vssue')
        // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
        config.plugins.push(['@vssue/vuepress-plugin-vssue', option]);
    return config;
};
